document.querySelector("#openaiText2SpeechVoice").addEventListener("change",e=>{let t=document.querySelector("#openaiText2SpeechVoicePreview audio");t.pause(),t.currentTime=0,t.setAttribute("src",`https://cdn.openai.com/API/docs/audio/${e.currentTarget.value}.wav`)}),document.querySelector("#openaiText2SpeechSpeed").addEventListener("input",e=>{let t=document.querySelector("label[for=openaiText2SpeechSpeed] span"),r=e.currentTarget.value;t.innerText=parseFloat(r).toFixed(2)});